-- ----------------------------
-- Table structure for `s_auth_assignment`
-- ----------------------------
DROP TABLE IF EXISTS `s_auth_assignment`;
CREATE TABLE `s_auth_assignment` (
  `item_name` varchar(64) COLLATE utf8_unicode_ci NOT NULL,
  `user_id` varchar(64) COLLATE utf8_unicode_ci NOT NULL,
  `created_at` int(11) DEFAULT NULL,
  PRIMARY KEY (`item_name`,`user_id`),
  CONSTRAINT `s_auth_assignment_ibfk_1` FOREIGN KEY (`item_name`) REFERENCES `s_auth_item` (`name`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

