  CONSTRAINT `s_auth_item_child_ibfk_1` FOREIGN KEY (`parent`) REFERENCES `s_auth_item` (`name`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `s_auth_item_child_ibfk_2` FOREIGN KEY (`child`) REFERENCES `s_auth_item` (`name`) ON DELETE CASCADE ON UPDATE CASCADE
