-- ----------------------------
-- Table structure for `s_story`
-- ----------------------------
DROP TABLE IF EXISTS `s_story`;
CREATE TABLE `s_story` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(50) DEFAULT NULL COMMENT '标题',
  `intro` text COMMENT '简介',
  `type` tinyint(4) DEFAULT '1' COMMENT '类型1图片 2视频',
  `cover_url` varchar(255) DEFAULT NULL COMMENT '封面图地址',
  `video_url` varchar(255) DEFAULT NULL COMMENT '视频地址',
  `created_at` int(11) DEFAULT NULL COMMENT '创建时间',
  `updated_at` int(11) DEFAULT NULL COMMENT '更新时间',
  `admin_id` int(11) DEFAULT '0' COMMENT '用户id',
  `next_updated_at` int(11) DEFAULT NULL COMMENT '下次更新时间',
  `current_chapters` mediumint(9) DEFAULT NULL COMMENT '当前章节',
  `total_chapters` mediumint(9) DEFAULT NULL COMMENT '总共章节',
  `is_show` tinyint(5) DEFAULT '1' COMMENT '是否显示1是 0否',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=11 DEFAULT CHARSET=utf8;

