  `from_uid` int(11) DEFAULT NULL COMMENT '评论用户id',
  `created_at` int(11) DEFAULT '0' COMMENT '评论时间',
  `comment_img` varchar(255) DEFAULT NULL COMMENT '评论图片',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

